<?phpheader('Content-Type: text/html; charset=utf-8');ini_set('display_errors', 'On');include_once 'config.php';if ( file_exists("languages/{$cfg['language']}.php") ) {  include_once "languages/{$cfg['language']}.php";} else {  die("Runtime error: couldn't load '{$cfg['language']}' language.");}// Current file name (without query string)$basename = array_shift(explode('?', basename($_SERVER['REQUEST_URI'])));include_once 'db_conn.php';include_once 'head.php';if ($basename != 'view.php') {  include_once 'menu.php';}